import React from "react";
import { Form, FormControl, Button } from "react-bootstrap";

export default class CNav extends React.Component {
  constructor(props) {
    super(props);
    this.state = { author: "", title: "", img: null, song: null };
    this.onFormSubmit = this.onFormSubmit.bind(this);
    this.onTitleChange = this.onTitleChange.bind(this);
    this.onAuthorChange = this.onTitleChange.bind(this);
    this.fileUpload = this.fileUpload.bind(this);
  }

  fileUpload(file) {
    // connect to db
    // push new row
    // store wav and img with id
    return { data: "we did it" };
  }

  onTitleChange(e) {
    console.log("changed");
    this.setState({ title: e.target.value });
    console.log(this.state);
  }

  onFormSubmit(e) {
    e.preventDefault();
    this.fileUpload(this.state).then(response => {
      console.log(response);
    });
  }

  render() {
    return (
      <Form
        onSubmit={this.handleSubmit}
        style={{ width: "50%", display: "inline-block", textAlign: "left" }}
      >
        <Form.Group controlId="title">
          <Form.Label>Title</Form.Label>
          <Form.Control
            type="text"
            onChange={this.onTitleChange}
            placeholder="Something Cool"
          />
        </Form.Group>

        <Form.Group controlId="author">
          <Form.Label>Author</Form.Label>
          <Form.Control type="text" placeholder="Beasty Boys" />
        </Form.Group>

        <Form.Group controlId="song">
          <Form.Label>Song File</Form.Label>
          <Form.Control type="file" />
        </Form.Group>

        <Form.Group controlId="image">
          <Form.Label>Song Image</Form.Label>
          <Form.Control type="file" />
        </Form.Group>

        <Button variant="primary" onClick={this.onFormSubmit}>
          Submit
        </Button>
      </Form>
    );
  }
}
